import {Component, OnInit} from '@angular/core';
import {NavigationEnd, Router} from "@angular/router";
import {filter} from "rxjs/operators";


@Component({
    selector: 'app-control-bar',
    templateUrl: './control-bar.component.html',
    styleUrls: ['./control-bar.component.css']
})
export class ControlBarComponent implements OnInit {
    constructor(private router: Router) {
    }

    public isUrlHome: boolean = false;
    public isUrlSearch: boolean = false;

    ngOnInit() {
        this.router.events.pipe(
            filter((event): event is NavigationEnd => event instanceof NavigationEnd)
        ).subscribe((event: NavigationEnd) => {
            this.isUrlHome = event.url === '/home';
            this.isUrlSearch = event.url === '/search';
        });
    }
}
<div class="control-bar">
	<img *ngIf="isUrlHome == false" src="./assets/icons/control-bar/home-icon-inactive.svg" alt="home icon">
	<img src="./assets/icons/control-bar/home-icon-active.svg" alt="home icon">
	<img src="./assets/icons/control-bar/search-icon-inactive.svg" alt="search icon">
	<img src="./assets/icons/control-bar/search-icon-active.svg" alt="">
</div>
