import {Component, OnInit} from '@angular/core';
import {ImagesService} from "../services/images.service";
import {CurrentUserService} from "../services/currentUser.service";
import {ProfilePhotoSaverService} from "../services/profile-photo-saver.service";

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent implements OnInit {

    public profilePhoto: Blob | undefined;

    constructor(private imagesService: ImagesService, private currentUserService: CurrentUserService, private profilePhotoService: ProfilePhotoSaverService) {
    }

    ngOnInit() {
        this.imagesService.getProfilePhoto(this.currentUserService.getItem('currentUser').username).subscribe(response => {
            this.profilePhotoService.saveProfilePhotoToSessionStorage('profilePhoto', response);
        });
    }

    getProfilePhoto(): string {
        return this.profilePhotoService.getProfilePhotoFromSessionStorage('profilePhoto');
    }

  isExpanded = false;

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }
}
<header>
	<nav
			class="navbar navbar-expand-sm navbar-toggleable-sm navbar-light bg-white border-bottom box-shadow mb-3"
	>
		<div class="container">

			<object [attr.data]="getProfilePhoto()" type="image/webp" class="pfp-thumbnail">
				<img class="default-profile-icon" src="./assets/icons/default-profile-icon/default-profile.svg" alt="default profile photo">
			</object>
			<button
					(click)="toggle()"
					[attr.aria-expanded]="isExpanded"
					aria-label="Toggle navigation"
					class="navbar-toggler"
					data-target=".navbar-collapse"
					data-toggle="collapse"
					type="button"
			>
				<span class="navbar-toggler-icon"></span>
			</button>
			<div
					[ngClass]="{ show: isExpanded }"
					class="navbar-collapse collapse d-sm-inline-flex justify-content-end"
			>
				<ul class="navbar-nav flex-grow">
					<li
							[routerLinkActiveOptions]="{ exact: true }"
							[routerLinkActive]="['link-active']"
							class="nav-item"
					>
						<a [routerLink]="['/home']" class="nav-link text-dark">Home</a>
					</li>
					<li [routerLinkActive]="['link-active']" class="nav-item">
						<a [routerLink]="['/counter']" class="nav-link text-dark"
						>Counter</a
						>
					</li>
					<li [routerLinkActive]="['link-active']" class="nav-item">
						<a [routerLink]="['/fetch-data']" class="nav-link text-dark"
						>Fetch data</a
						>
					</li>
				</ul>
			</div>
		</div>
	</nav>
</header>
